$(document).ready(function(){
    // bind event handlers when the page loads.
    bindEvents();
});

function bindEvents() {
    $(function () {
        $('#dtp').datetimepicker({format : 'DD/MM/YYYY HH:mm'});
        $('.selectpicker').selectpicker();
        $(document).on('focus',"#pickers", function(){
            $(this).datetimepicker({format : 'DD/MM/YYYY HH:mm'});
        });

    });
    $(".create-todo").on('click', function(event){
        event.preventDefault();
         $todo    = $(this),
         content  = $('.todo-input').val(),
         deadline = $('.due').val(),
         priority = $('.priority').val();
         deadline = moment(deadline, "DD-MM-YYYY HH:mm").format("YYYY-MM-DD HH:MM");
         if (deadline == "Invalid date") {
            deadline = null;
         }

        if(content.length !== 0) {
        $.post("todos/add", {content: content, deadline: deadline, priority: priority},
            function(data){
                if(data.response === true){
                   $.post("todos", null,
                        function(data) {               
                        $('.content').html($('.content', data));
                      
                        bindEvents();
                      });
                } else {
                    // print error message
                    console.log('could not add');
                }
            }, 'json');
        } else { 
            console.log('could not add');
        } 
    });
    $('.todoItem').on('click', 'button .entypo-check', function(event){
        event.preventDefault();
        var $todo     = $(this).parent().parent(),
        update_id     = $todo.children('.todo').attr('id');
        update_id     = update_id.replace("todo-","");
        
        $todo.toggleClass("done");

        if($todo.hasClass("done")) {
        $.post("todos/update", {id: update_id, completed: 1},
            function(data){
            if(data.response == false){
                // print error message
                console.log('could not update');
            }
        }, 'json');
        } else {
            $.post("todos/update", {id: update_id, completed: 0},
            function(data){
            if(data.response == false){
                // print error message
                console.log('could not update');
            }
        }, 'json');
        }
    });

    $('.todoItem').on('click', 'button.delete-todo',function(event){
        event.preventDefault();
        var $todo = $(this),
        remove_id = $(this).attr('id');
        remove_id = remove_id.replace("remove-","");

        $.post("todos/remove", {
            id: remove_id
        },
        function(data){
            if(data.response == true) {
                $todo.parent().remove();
            } else {
                console.log('could not remove ');
            }
        }, 'json');
    });

    $('.todos').on('keyup', '.todo', function(event){
        var $todo      = $(this),
        update_id  = $todo.attr('id'),
        update_content = $todo.text();
        
        update_id = update_id.replace("todo-","");
     
        $.post("todos/update", {id: update_id, content: update_content},
            function(data){
            if(data.response == false){
                console.log('could not update' + data);
            }
        }, 'json');

    });
}

